import 'dart:math';

import 'package:shared_app/domain.dart';
import 'package:shared_app/presentation.dart';

final class CollectionViewer extends StatefulWidget {
  const CollectionViewer(
    this.skeleton, {
    this.onPressed,
    this.onChangeImage,
    this.onChangeTitle,
    this.onChangeUrl,
    this.onSetPreviewFromUrl,
  });

  final List<List<CollectionLink>> skeleton;
  final void Function(CollectionLink link)? onPressed;
  final void Function(CollectionLink link)? onChangeImage;
  final void Function(CollectionLink link, String title)? onChangeTitle;
  final void Function(CollectionLink link, String url)? onChangeUrl;
  final void Function(CollectionLink link)? onSetPreviewFromUrl;

  @override
  State<CollectionViewer> createState() => _CollectionViewerState();
}

final class _CollectionViewerState extends State<CollectionViewer> {
  Map<int, bool>? isDoubleLines;

  @override
  Widget build(BuildContext context) {
    isDoubleLines = {
      for (int i = 0; i < widget.skeleton.length; i++) i: false,
    };

    return Padding(
      padding: const EdgeInsets.all(20),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.stretch,
        children: [
          for (int i = 0; i < widget.skeleton.length; i++) ...[
            if (widget.skeleton[i].length > 4)
              Column(
                crossAxisAlignment: CrossAxisAlignment.stretch,
                children: [
                  Text(
                    widget.skeleton[i].first.type.translate(context, true),
                    style: TextStyle(
                      fontFamily: 'Cygre',
                      fontSize: 26,
                      fontWeight: FontWeight.w500,
                      height: 42.04 / 26,
                    ),
                  ),
                  const SizedBox(height: 21),
                  GridView.builder(
                    shrinkWrap: true,
                    physics: const NeverScrollableScrollPhysics(),
                    gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                      crossAxisCount: 4,
                      crossAxisSpacing: 30,
                      mainAxisSpacing: 30,
                      childAspectRatio: 40 / 41,
                    ),
                    itemCount: widget.skeleton[i].length,
                    itemBuilder: (context, index) {
                      final link = widget.skeleton[i][index];
                      return _LinkWidget(
                        link,
                        aspectRatio: 40 / 41,
                        titleMaxLines: 1,
                        onPressed: widget.onPressed == null
                            ? null
                            : () => widget.onPressed!(link),
                        onChangeImage: widget.onChangeImage == null
                            ? null
                            : () => widget.onChangeImage!(link),
                        onChangeTitle: widget.onChangeTitle == null
                            ? null
                            : (title) => widget.onChangeTitle!(link, title),
                        onChangeUrl: widget.onChangeUrl == null
                            ? null
                            : (url) => widget.onChangeUrl!(link, url),
                        onSetPreviewFromUrl: widget.onSetPreviewFromUrl == null
                            ? null
                            : () => widget.onSetPreviewFromUrl!(link),
                      );
                    },
                  ),
                ],
              ),
            if (widget.skeleton[i].length <= 4)
              Row(
                children: [
                  for (int j = 0; j < max(widget.skeleton[i].length, 3); j++)
                    Builder(
                      builder: (context) {
                        if (j >= widget.skeleton[i].length) {
                          return Expanded(child: const SizedBox());
                        }

                        final link = widget.skeleton[i][j];

                        final linksWithCurrentType = widget.skeleton[i]
                            .where((e) => e.type == link.type);
                        final isFirst = linksWithCurrentType.first == link;

                        return Expanded(
                          child: Column(
                            crossAxisAlignment: CrossAxisAlignment.start,
                            children: [
                              Text(
                                link.type.translate(
                                  context,
                                  linksWithCurrentType.length > 1,
                                ),
                                style: TextStyle(
                                  fontFamily: 'Cygre',
                                  fontSize: 26,
                                  fontWeight: FontWeight.w500,
                                  height: 42.04 / 26,
                                  color: isFirst
                                      ? Colors.black
                                      : Colors.transparent,
                                ),
                              ),
                              const SizedBox(height: 21),
                              LayoutBuilder(builder: (context, constraints) {
                                final linesInTextPainter = TextPainter(
                                  text: TextSpan(
                                    text: link.title,
                                    style: TextStyle(
                                      fontFamily: 'Cygre',
                                      fontSize: 26,
                                      fontWeight: FontWeight.w500,
                                      height: 42.04 / 26,
                                      color: isFirst
                                          ? Colors.black
                                          : Colors.transparent,
                                    ),
                                  ),
                                  textDirection: TextDirection.ltr,
                                )..layout(maxWidth: constraints.maxWidth + 90);

                                final linesCount = linesInTextPainter
                                    .computeLineMetrics()
                                    .length;

                                if (!isDoubleLines![i]!)
                                  isDoubleLines![i] = linesCount > 1;

                                return _LinkWidget(
                                  link,
                                  aspectRatio: 165 / 136,
                                  titleMaxLines:
                                      widget.skeleton[i].length > 3 ||
                                              !isDoubleLines![i]!
                                          ? 1
                                          : 2,
                                  onPressed: widget.onPressed == null
                                      ? null
                                      : () => widget.onPressed!(link),
                                  onChangeImage: widget.onChangeImage == null
                                      ? null
                                      : () => widget.onChangeImage!(link),
                                  onChangeTitle: widget.onChangeTitle == null
                                      ? null
                                      : (title) =>
                                          widget.onChangeTitle!(link, title),
                                  onChangeUrl: widget.onChangeUrl == null
                                      ? null
                                      : (url) => widget.onChangeUrl!(link, url),
                                  onSetPreviewFromUrl:
                                      widget.onSetPreviewFromUrl == null
                                          ? null
                                          : () =>
                                              widget.onSetPreviewFromUrl!(link),
                                );
                              }),
                            ],
                          ),
                        );
                      },
                    ),
                ].separated(SizedBox(width: 30)),
              ),
          ],
        ].separated(SizedBox(height: 60)),
      ),
    );
  }
}

final class _LinkWidget extends StatefulWidget {
  const _LinkWidget(
    this.link, {
    required this.aspectRatio,
    required this.titleMaxLines,
    required this.onPressed,
    required this.onChangeImage,
    required this.onChangeTitle,
    required this.onChangeUrl,
    required this.onSetPreviewFromUrl,
  });

  final CollectionLink link;
  final double aspectRatio;
  final int titleMaxLines;
  final VoidCallback? onPressed;
  final VoidCallback? onChangeImage;
  final ValueChanged<String>? onChangeTitle;
  final ValueChanged<String>? onChangeUrl;
  final VoidCallback? onSetPreviewFromUrl;

  @override
  State<_LinkWidget> createState() => _LinkWidgetState();
}

final class _LinkWidgetState extends State<_LinkWidget> {
  bool isHovered = false;

  @override
  Widget build(BuildContext context) {
    return MouseRegion(
      onEnter: (_) => setState(() => isHovered = true),
      onExit: (_) => setState(() => isHovered = false),
      child: AspectRatio(
        aspectRatio: widget.aspectRatio,
        child: Clickable(
          onTap: widget.onPressed,
          isDisabling: false,
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.stretch,
            mainAxisSize: MainAxisSize.min,
            children: [
              Expanded(
                child: Clickable(
                  onTap: widget.onChangeImage,
                  isDisabling: false,
                  child: widget.link.previewImageUrl == null ||
                          widget.link.previewImageUrl!.toString().isEmpty
                      ? Container(
                          color: Color(0xFFC3C3C3),
                          child: Center(
                            child: Icon(
                              Icons.image_not_supported,
                              size: 80,
                              color: Colors.white,
                            ),
                          ),
                        )
                      : Image.network(
                          widget.link.previewImageUrl.toString(),
                          fit: BoxFit.cover,
                        ),
                ),
              ),
              Stack(
                children: [
                  SizedBox(
                    height: widget.titleMaxLines > 1 ? 85 : 66,
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.stretch,
                      children: [
                        const SizedBox(height: 12),
                        ConstrainedBox(
                          constraints: const BoxConstraints(maxHeight: 38),
                          child: TextWithEditing(
                            widget.link.title
                                .replaceAll(RegExp(_emojiRegular), '')
                                .trim(),
                            style: TextStyle(
                              fontFamily: 'Cygre',
                              color: Color(0xFF000000),
                              fontSize: 16,
                              fontWeight: FontWeight.w500,
                              height: 19.12 / 16,
                            ),
                            maxLines: widget.titleMaxLines,
                            onChanged: widget.onChangeTitle,
                          ),
                        ),
                        const SizedBox(height: 6),
                        TextWithEditing(
                          widget.link.url.host.replaceAll('www.', ''),
                          changingText: '${widget.link.url}',
                          style: TextStyle(
                            fontFamily: 'Cygre',
                            color: Color(0xFF747474),
                            fontSize: 15,
                            fontWeight: FontWeight.w500,
                            height: 18 / 15,
                          ),
                          maxLines: 1,
                          onChanged: widget.onChangeUrl,
                        ),
                        const SizedBox(height: 8),
                      ],
                    ),
                  ),
                  if (isHovered)
                    Positioned(
                      bottom: 12,
                      right: 15,
                      child: Row(
                        children: [
                          if (widget.onSetPreviewFromUrl != null)
                            Clickable(
                              onTap: widget.onSetPreviewFromUrl,
                              child: Icon(
                                Icons.search,
                                size: 15,
                              ),
                            ),
                        ].separated(SizedBox(width: 10)),
                      ),
                    ),
                ],
              ),
            ],
          ),
        ),
      ),
    );
  }
}

